<script>
    $(document).ready(function () {
        var droplist = $('#studentid');
        droplist.change(function () {
            $('#finish_table tbody').empty();
            $('#studentlist tbody').empty();
            $('#paid tbody').empty();
            $('#studentdetails').empty();
            $.ajax({
                type: "POST",
                url: "studentdetails",
                data: {studentid: $('#studentid option:selected').val()},
                dataType: "html",
                success: function (data) {
                    $('#studentdetails').append(data);
                    $('#studentdetail').show("slow");
                }
            });
            $.ajax({
                type: "POST",
                url: "feepopulatestudent",
                data: {courseid: $('#Feesallocation_courseid option:selected').val(), batchid: $('#Feesallocation_batchid option:selected').val(), studentid: $('#studentid option:selected').val()},
                dataType: "html",
                success: function (data) {
                    $('#finish_table tbody').append(data);
                    $('#finish_table').show("slow");
                    $('#paymenttable').show("slow");
                    $.ajax({
                        type: "POST",
                        url: "paiddetails",
                        data: {courseid: $('#Feesallocation_courseid option:selected').val(), batchid: $('#Feesallocation_batchid option:selected').val(), studentid: $('#studentid option:selected').val()},
                        dataType: "html",
                        success: function (data) {
                            $('#paidlist').show("slow");
                            $('#paid tbody').append(data);
                        }
                    });
                }
            });
        })
    });

    function getfeedetails() {
        if (document.getElementById('hidden-field').value === "") {
            alert("Please select student");
            return;
        }
        $('#finish_table tbody').empty();
        $('#studentlist tbody').empty();
        $('#paid tbody').empty();
        $('#studentdetails').empty();
        $.ajax({
            type: "POST",
            url: "studentdetails",
            data: {studentname: document.getElementById('hidden-field').value},
            dataType: "html",
            success: function (data) {
                $('#studentdetails').append(data);
                $('#studentdetail').show("slow");
            }
        });
        $.ajax({
            type: "POST",
            url: "feepopulatestudent",
            data: {studentname: document.getElementById('hidden-field').value},
            dataType: "html",
            success: function (data) {
                $('#finish_table tbody').append(data);
                $('#finish_table').show("slow");
                $('#paymenttable').show("slow");
                $.ajax({
                    type: "POST",
                    url: "paiddetails",
                    data: {studentname: document.getElementById('hidden-field').value},
                    dataType: "html",
                    success: function (data) {
                        $('#paidlist').show("slow");
                        $('#paid tbody').append(data);
                    }
                });
            }
        });
    }
    $('#modeofpay').change(function () {
        if ($('#modeofpay option:selected').val() === '1') {
            $('#bankname').show("slow");
            $('#cheque_no').show("slow");
            $('#cheque_date').show("slow");
        } else {
            $('#bankname').hide("slow");
            $('#cheque_no').hide("slow");
            $('#cheque_date').hide("slow");
        }

        $('#totalamount').empty();
        var amount = 0;
        var fine = 0;
        var discount = 0;
        $('#finish_table tbody tr').each(function (row, tr) {
            if ($(this).find(":checkbox").prop("checked")) {
                amount += parseInt($(tr).find('td:eq(3) input').val());
                if ($(tr).find('td:eq(4) input').val() != "") {
                    fine += parseInt($(tr).find('td:eq(4) input').val());
                }
                if ($(tr).find('td:eq(5) input').val() != "") {
                    discount += parseInt($(tr).find('td:eq(5) input').val());
                }
            }
        });
        if (discount != "") {
            $('#totalamount').append(parseInt(fine) + parseInt(amount) - parseInt(discount));
        } else if (fine != "") {
            $('#totalamount').append(parseInt(fine) + parseInt(amount));
        } else {
            $('#totalamount').append(parseInt(amount));
        }
    });
    $('#remarks').change(function () {
        $('#totalamount').empty();
        var amount = 0;
        var fine = 0;
        var discount = 0;
        $('#finish_table tbody tr').each(function (row, tr) {
            if ($(this).find(":checkbox").prop("checked")) {
                amount += parseInt($(tr).find('td:eq(3) input').val());
                if ($(tr).find('td:eq(4) input').val() != "") {
                    fine += parseInt($(tr).find('td:eq(4) input').val());
                }
                if ($(tr).find('td:eq(5) input').val() != "") {
                    discount += parseInt($(tr).find('td:eq(5) input').val());
                }
            }
        });
        if (discount != "") {
            $('#totalamount').append(parseInt(fine) + parseInt(amount) - parseInt(discount));
        } else if (fine != "") {
            $('#totalamount').append(parseInt(fine) + parseInt(amount));
        } else {
            $('#totalamount').append(parseInt(amount));
        }
    });

    $(document).ready(function () {
        $("#fine").keydown(function (e) {
            // Allow: backspace, delete, tab, escape, enter and .
            if ($.inArray(e.keyCode, [46, 8, 9, 27, 13, 110, 190]) !== -1 ||
                    // Allow: Ctrl+A, Command+A
                            (e.keyCode == 65 &amp;&amp; (e.ctrlKey === true || e.metaKey === true)) ||
                            // Allow: home, end, left, right, down, up
                                    (e.keyCode &gt;= 35 &amp;&amp; e.keyCode &lt;= 40)) {
                        // let it happen, don't do anything
                        return;
                    }
                    // Ensure that it is a number and stop the keypress
                    if ((e.shiftKey || (e.keyCode &lt; 48 || e.keyCode &gt; 57)) &amp;&amp; (e.keyCode &lt; 96 || e.keyCode &gt; 105)) {
                        e.preventDefault();
                    }
                });
    });
    function savepayment() {
        var studentid = $('#studentid option:selected').val();
        var studentname = document.getElementById('hidden-field').value;
        var courseid = $('#Feesallocation_courseid option:selected').val();
        var batchid = $('#Feesallocation_batchid option:selected').val();
        var modeofpay = $('#modeofpay option:selected').val();
        var bankname = $('#bank_name option:selected').val();
        var chequeno = $('#chequeno').val();
        var chequedate = $('#chequedate').val();
        var remarks = $('#remarks').val();
        var receiptno = $('#receiptno').val();
        var student = [];
        var notcheck = "";
        var check = "";
        $('#finish_table tbody tr').each(function (row, tr) {
            if ($(this).find(":checkbox").prop("checked")) {
                var feessubcategoryid = $(tr).find('td:eq(0)').data('id');
                var feestype = $(tr).find('td:eq(1) option:selected').val();
                var amount = $(tr).find('td:eq(3) input').val();
                var fine = $(tr).find('td:eq(4) input').val();
                var discount = $(tr).find('td:eq(5) input').val();

                student.push(feessubcategoryid);
                student.push(feestype);
                student.push(amount);
                student.push(fine);
                student.push(discount);
                check = "1";
            } else {
                notcheck = "1";
            }
        });
        if (modeofpay === "") {
            alert("please select Payment mode");
            return;
        }
        if (check === "") {
            alert("please select checkbox");
            return;
        }
        var sendarray = JSON.stringify(student);
        $.ajax({
            type: "POST",
            url: "paymentdetailsforcollection",
            data: {courseid: courseid, batchid: batchid, studentid: studentid, remarks: remarks, modeofpay: modeofpay, sendarray: sendarray, bankname: bankname, chequeno: chequeno, chequedate: chequedate, studentname: studentname, receiptno: receiptno},
            dataType: "html",
            success: function (data) {
                if (data === "error") {
                    alert("Already Paid");
                } else {
                    $('#paid tbody').empty();
                    $.ajax({
                        type: "POST",
                        url: "paiddetails",
                        data: {courseid: $('#Feesallocation_courseid option:selected').val(), batchid: $('#Feesallocation_batchid option:selected').val(), studentid: $('#studentid option:selected').val()},
                        dataType: "html",
                        success: function (data) {
                            $('#paidlist').show("slow");
                            $('#paid tbody').append(data);
                        }
                    });
                    alert("Fees successfully paid");
                    if ($('#checkbox1').is(':checked')) {
                        var sendarray = JSON.stringify(data);
                        window.open("/index.php/core/feetemplate/view3/?feeid=" + sendarray);
                    }
                    location.reload();
                }
            }
        });
    }
    $(document).ready(function () {
        var droplist = $('#Feesallocation_studentid1');
        droplist.change(function () {
            $('#paid tbody').empty();
            $.ajax({
                type: "POST",
                url: "paiddetails",
                data: {courseid: $('#Feesallocation_courseid1 option:selected').val(), batchid: $('#Feesallocation_batchid1 option:selected').val(), feescategoryid: $('#Feesallocation_feescategoryid1 option:selected').val(), studentid: $('#Feesallocation_studentid1 option:selected').val()},
                dataType: "html",
                success: function (data) {
                    $('#paidlist').show("slow");
                    $('#paid tbody').append(data);
                }
            });
        })
    });
    function generatereceipt() {
        var subid;
        var studentids;
        $('#paid tbody tr').each(function (row, tr) {
            if ($(this).find(":checkbox").prop("checked")) {
                var student = $(tr).find('td:eq(2)').data('id');
                var feessubcategoryid = $(tr).find('td:eq(0)').data('id');
                studentids = student;
                subid = subid + "," + feessubcategoryid;
            }
        });

        var studentid = studentids;
        var sendarray = JSON.stringify(subid);

        window.open("/index.php/core/feetemplate/view2/?feeid=" + sendarray + "&amp;studentid=" + studentid);
    }
    function deletefee() {

        var students = [];
        var check = "";
        $('#paid tbody tr').each(function (row, tr) {
            if ($(this).find(":checkbox").prop("checked")) {
                var student = $(tr).find('td:eq(2)').data('id');
                var feessubcategoryid = $(tr).find('td:eq(0)').data('id');
                students.push(feessubcategoryid);
                students.push(student);
                check = '1';
            }
        });
        var sendarray = JSON.stringify(students);
        if (check === "") {
            alert("Please Select Checkbox");
            return;
        } else {
            var result = confirm("Want to delete?");
            if (result) {
                $.ajax({
                    type: "POST",
                    url: "deletefees",
                    data: {sendarray: sendarray},
                    dataType: "html",
                    success: function (data) {
                        alert("Successfully deleted");
                        $('#paid tbody').empty();
                        $.ajax({
                            type: "POST",
                            url: "paiddetails",
                            data: {courseid: $('#Feesallocation_courseid option:selected').val(), batchid: $('#Feesallocation_batchid option:selected').val(), studentid: $('#studentid option:selected').val()},
                            dataType: "html",
                            success: function (data) {
                                $('#paidlist').show("slow");
                                $('#paid tbody').append(data);
                            }
                        });
                    }
                });
            }
        }
    }
</script>
	

		
